                f<span class="token punctuation">[</span>j<span class="token punctuation">]</span> <span class="token operator">|=</span>  f<span class="token punctuation">[</span>j <span class="token operator">-</span> x<span class="token punctuation">]</span><span class="token punctuation">;</span>
            <span class="token punctuation">}</span>
        <span class="token punctuation">}</span>
        <span class="token keyword">return</span> f<span class="token punctuation">[</span>m<span class="token punctuation">]</span> <span class="token operator">!=</span> <span class="token number">0</span><span class="token punctuation">;</span>
