        <span class="token keyword">while</span> <span class="token punctuation">(</span>seq<span class="token punctuation">[</span>k<span class="token punctuation">]</span> <span class="token operator">&lt;</span> root<span class="token punctuation">)</span> <span class="token punctuation">{</span>
